color: 'limegreen',
},
{
color: 'purple',
},
{
color: 'red',
},
]

return (
&lt;StyledDiscoFloor&gt;
{squares.map((sqr,i) =&gt; (
&lt;div
className=&quot;grid-item&quot;
style={{
backgroundColor: sqr.color,
animationDelay: `${String(i)}s`,

}}&gt;

&lt;/div&gt;
))}
