&lt;!-- Styles --&gt;	
&lt;style&gt;
.example1 {
	 height: 50px;	
	 overflow: hidden;
	 position: relative;
}

.example1 h3 {
	 font-size: 3em;
	 color: limegreen;
	 position: absolute;
	 width: 100%;
	 height: 100%;
	 margin: 0;
	 line-height: 50px;
	 text-align: center;

	 transform:translateX(100%);

	 animation: example1 15s linear infinite;
}

@keyframes example1 {
	 0% {

		 transform: translateX(100%); 		
	 }
	 100% {

		 transform: translateX(-100%);
	 }
}
&lt;/style&gt;

