export function appendDateForSorting(time:string) {

switch (true) {
	// you could omit this first case since the default does the same thing
case time &gt;= '04:00':
return '2024-01-01T' + time

case time &lt; '03:59':
return '2024-01-02T' + time

default:
return '2024-01-01T' + time
}
}

